import { Card } from 'antd';

import {
  HeartIcon,
  ArrowLeftIcon,
  ArrowRightIcon,
  RefreshIcon,
} from '@heroicons/react/outline';
import { Id, User, WithId, WithIsLiked, WithPhoto } from '../../app/types';
import { baseURL } from '../../app/api';

interface Props {
  type: 'front' | 'back';
  user: (User & WithId & WithPhoto & WithIsLiked) | null;
  isLoading: boolean;

  onLike: (id: Id) => void;
  onNext: () => void;
  onBack: () => void;
  onRefresh: () => void;
  disablebuttons?: boolean;
}

export const Side: React.FC<Props> = ({
  type,
  user,
  isLoading,
  onBack,
  onLike,
  onNext,
  onRefresh,
  disablebuttons = false,
}) => {
  const likeHandler = () => {
    if (user) {
      onLike(user.id);
    }
  };

  if (!user) {
    return (
      <Card
        className="w-96  absolute top-1/2 -left-0"
        style={
          type === 'front'
            ? {
                backfaceVisibility: 'hidden',
                zIndex: 10,
                transform: 'translateY(-50%)',
              }
            : {
                transform: 'translateY(-50%) rotateY(180deg)',
                backfaceVisibility: 'hidden',
              }
        }
        bordered
        cover={
          <div className="w-full h-60 flex justify-center">
            <img
              className="w-full h-full object-cover"
              src={
                'data:image/png;base64,'
              }
              alt="user"
            />
          </div>
        }
        actions={[
          <button
            className="block w-full h-full"
            disabled={disablebuttons}
            onClick={onRefresh}
          >
            <RefreshIcon className="h-8 w-8 mx-auto" />
          </button>,
        ]}
      >
        <Card.Meta
          title={`There are no users more`}
          description={<div className="h-32 overflow-y-scroll"></div>}
        />
      </Card>
    );
  }

  return (
    <Card
      className="w-96 absolute top-1/2 -left-0"
      style={
        type === 'front'
          ? {
              backfaceVisibility: 'hidden',
              zIndex: 10,
              transform: 'translateY(-50%)',
            }
          : {
              transform: 'translateY(-50%) rotateY(180deg)',
              backfaceVisibility: 'hidden',
            }
      }
      bordered
      cover={
        <div className="w-full h-60 flex justify-center">
          <img
            className="w-full h-full object-cover"
            src={
              user.photo
                ? baseURL + user.photo
                : 'data:image/png;base64,'
            }
            alt="user"
          />
        </div>
      }
      actions={[
        <button
          className="block w-full h-full"
          onClick={onBack}
          disabled={disablebuttons || isLoading}
        >
          <ArrowLeftIcon className="h-8 w-8 mx-auto" />
        </button>,

        <button
          className="block w-full h-full group"
          onClick={likeHandler}
          disabled={disablebuttons || isLoading}
        >
          <HeartIcon
            className={`h-8 w-8 mx-auto ${
              user.is_liked
                ? 'fill-red-500 text-red-500'
                : 'group-hover:text-red-500'
            }`}
          />
        </button>,

        <button
          className="block w-full h-full"
          onClick={onNext}
          disabled={disablebuttons || isLoading}
        >
          <ArrowRightIcon className="h-8 w-8 mx-auto" />
        </button>,
      ]}
    >
      <Card.Meta
        title={`${user.first_name} ${user.last_name}`}
        description={
          <div className="h-32 overflow-y-scroll">{user.description}</div>
        }
      />
    </Card>
  );
};
